mapper.select:
SELECT * FROM ( SELECT A.*, ROWNUM RN FROM ( SELECT b.seq_comment , c.category_name, b.comment_content, b.comment_date, c.category_pk, u.seq_category, b.seq_board FROM BOARD_COMMENT B, BOARD U, category C WHERE u.seq_category = c.seq_category AND U.SEQ_BOARD = B.SEQ_BOARD AND b.comment_id = #{comment_id}
and b.comment_content LIKE '%'|| #{search_keyword} ||'%'
order by b.seq_comment desc ) A ) WHERE RN BETWEEN #{start} AND #{end}

SELECT * FROM ( SELECT A.*, (SELECT COUNT(*) FROM BOARD_COMMENT cm WHERE cm.seq_board = A.seq_board) cm_count, ROWNUM RN FROM ( SELECT u.seq_board, c.category_name, u.board_title, u.view_count , u.written_date, c.category_pk, u.seq_category FROM BOARD U, category C WHERE u.seq_category = c.seq_category AND u.member_id = #{member_id}
and u.${search_type} LIKE '%'|| #{search_keyword} ||'%'
order by u.seq_board desc ) A ) WHERE RN BETWEEN #{start} AND #{end}

SELECT count(seq_submit) FROM VOL_BOARD A, VOL_SUBMIT B WHERE A.SEQ_BOARD = B.SEQ_BOARD AND a.member_id = #{member_id}
AND a.${search_type} LIKE '%'|| #{search_keyword} ||'%'
AND b.${search_type} LIKE '%'|| #{search_keyword} ||'%'

SELECT * FROM ( SELECT C.*, ROWNUM RN FROM (SELECT A.SEQ_BOARD,A.VOL_DEADLINE, b.member_id, a.board_title, b.writer_name, B.WRITER_PHONE, A.VOL_COUNT, B.vol_status, B.seq_submit, (SELECT COUNT(VOL_STATUS) FROM VOL_SUBMIT B WHERE B.SEQ_BOARD = A.SEQ_BOARD AND B.VOL_STATUS = 'Y') select_COUNT, (SELECT COUNT(SEQ_SUBMIT) FROM VOL_SUBMIT B WHERE B.SEQ_BOARD = A.SEQ_BOARD) signup_count FROM VOL_BOARD A, VOL_SUBMIT B WHERE A.SEQ_BOARD = B.SEQ_BOARD AND a.member_id = #{member_id}
AND a.${search_type} LIKE '%'|| #{search_keyword} ||'%'
AND b.${search_type} LIKE '%'|| #{search_keyword} ||'%'
ORDER BY a.seq_board DESC) C) WHERE RN BETWEEN #{start} AND #{end}

		select count(seq_pay) FROM pay P, support_board S WHERE P.seq_board = S.seq_BOARD AND s.member_id = #{member_id}


         		and s.${search_type} LIKE '%'|| #{search_keyword} ||'%'


         		and p.${search_type} LIKE '%'|| #{search_keyword} ||'%'

SELECT * FROM ( SELECT A.*, ROWNUM RN FROM ( SELECT p.seq_pay, p.seq_board, s.board_title, p.member_id, p.pay_money, p.pay_date FROM pay P, support_board S WHERE P.seq_board = S.seq_BOARD AND s.member_id = #{member_id}
and s.${search_type} LIKE '%'|| #{search_keyword} ||'%'
and p.${search_type} LIKE '%'|| #{search_keyword} ||'%'
order by P.seq_pay desc ) A ) WHERE RN BETWEEN #{start} AND #{end}

SELECT * FROM ( SELECT C.*, ROWNUM RN FROM (SELECT A.SEQ_BOARD,A.VOL_DEADLINE, b.member_id, a.board_title, A.VOL_COUNT, B.vol_status, B.seq_submit, (SELECT COUNT(VOL_STATUS) FROM VOL_SUBMIT B WHERE B.SEQ_BOARD = A.SEQ_BOARD AND B.VOL_STATUS = 'Y') select_COUNT, (SELECT COUNT(SEQ_SUBMIT) FROM VOL_SUBMIT B WHERE B.SEQ_BOARD = A.SEQ_BOARD) signup_count FROM VOL_BOARD A, VOL_SUBMIT B WHERE A.SEQ_BOARD = B.SEQ_BOARD AND b.member_id = #{member_id}
AND a.${search_type} LIKE '%'|| #{search_keyword} ||'%'
ORDER BY a.seq_board DESC) C) WHERE RN BETWEEN #{start} AND #{end}

		SELECT * 
		FROM (
		SELECT A.*, ROWNUM RN
		FROM  (
		    SELECT  p.seq_pay, p.seq_board, s.board_title, p.member_id, p.pay_money, p.pay_date
		    FROM pay P, support_board S
		        WHERE P.seq_board = S.seq_BOARD
		        AND p.member_id = #{member_id}
                order by P.seq_pay desc 
		    ) A
		)
         WHERE RN BETWEEN #{start} AND #{end}
