mapper.select:
select b.*, (select nvl(count(SEQ_BOARD), 0) from VOL_SUBMIT where SEQ_BOARD = b.SEQ_BOARD) as cur, (select FILES_SYS from ( select * from VOL_FILES order by 1) where SEQ_BOARD = b.SEQ_BOARD and ROWNUM = 1) as files_sys from (select a.*, rownum as num from (select * from VOL_BOARD where
${category} like '%' || #{search} || '%'
order by 1 desc) a where rownum <= #{end}) b where num >= #{start}

mapper.update:
update VOL_BOARD
set VIEW_COUNT = VIEW_COUNT + 1
where seq_board = #{seq_board}

update VOL_BOARD
set board_title   = #{board_title},
    board_content = #{board_content},
    VOL_DEADLINE  = #{vol_deadLine},
    vol_count     = #{vol_count}
where SEQ_BOARD = #{seq_board}
